import React, { Component } from 'react';
import { Text, View,Image } from 'react-native';

class Home extends Component {
    state = {
        users:[]
    }
    componentDidMount() {
        if(this.props.users.length > 1){
            let arr = this.props.users
            this.setState({
                users:arr
            })
        }else{
            let arr = this.props.users
            this.setState({
                users:[{}]
            })
        }
    }


    cardRenderer = () => {
        if(this.props.users.length > 1){
        return this.props.users.map(user => {
            user_img = user.img_url
            return(
                    <View key={user.id}>
                    <Image source={user}/>
                    <Text>{user.username}</Text>
                    </View>)
        }
                )
        }
    }

    render() {

        return (
            <View>
                <View>{this.cardRenderer()}</View>
            </View>
        );
    }
}

export default Home;
